package main

import (
	"fmt"
	"strings"
)

func main() {
	fmt.Println(countFloors(input))
	fmt.Println(indexBasement(input))
}

func indexBasement(input string) int {
	input = strings.TrimSpace(input)

	var floor int
	for i, ch := range input {
		if ch == '(' {
			floor++
		} else {
			floor--
		}
		if floor == -1 {
			return i + 1
		}
	}
	return -1
}
func countFloors(input string) int {
	input = strings.TrimSpace(input)

	var floor int
	for _, ch := range input {
		if ch == '(' {
			floor++
		} else {
			floor--
		}
	}
	return floor
}

